import React, { useState, useEffect, useMemo } from 'react';
import { useTimer } from '../../hooks/useTimer';
import quizService from '../../services/quiz';

const QuizPlayer = ({ quiz, onFinish }) => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answers, setAnswers] = useState({});
    const [selectedChoice, setSelectedChoice] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [participantName, setParticipantName] = useState('');
    const [hasStarted, setHasStarted] = useState(false);
    const [showResults, setShowResults] = useState(false);
    const [quizResults, setQuizResults] = useState(null);

    const currentQuestion = useMemo(() => quiz.questions[currentQuestionIndex], [quiz, currentQuestionIndex]);

    const { timeLeft, startTimer, resetTimer } = useTimer(
        currentQuestion?.time_per_question || 30,
        () => handleNextQuestion(true) // auto-submit on time up
    );

    useEffect(() => {
        if (hasStarted && currentQuestion) {
            resetTimer(currentQuestion.time_per_question || quiz.time_per_question || 30);
            startTimer();
            setSelectedChoice(null);
        }
    }, [currentQuestion, hasStarted, quiz.time_per_question, resetTimer, startTimer]);

    const handleAnswerSelect = (choiceId) => {
        setSelectedChoice(choiceId);
    };

    const handleNextQuestion = (isTimeUp = false) => {
        const answer = isTimeUp ? null : selectedChoice;
        setAnswers(prev => ({ ...prev, [currentQuestion.id]: answer }));

        if (currentQuestionIndex < quiz.questions.length - 1) {
            setCurrentQuestionIndex(prev => prev + 1);
        } else {
            submitQuiz();
        }
    };

    const submitQuiz = async () => {
        setIsSubmitting(true);
        const finalAnswers = { ...answers };
        if (selectedChoice) {
            finalAnswers[currentQuestion.id] = selectedChoice;
        }

        try {
            const response = await quizService.submitAttempt(quiz.slug, {
                participant_name: participantName,
                answers: finalAnswers,
                time_taken: 1 // placeholder
            });
            setQuizResults(response.data);
            setShowResults(true);
            onFinish(response.data.score);
        } catch (error) {
            console.error('Error submitting quiz:', error);
            alert('There was an error submitting your quiz.');
        } finally {
            setIsSubmitting(false);
        }
    };
    
    const handleStart = (e) => {
        e.preventDefault();
        if(participantName.trim()){
            setHasStarted(true);
        }
    }

    if (!hasStarted) {
        return (
            <div className="text-center">
                <form onSubmit={handleStart}>
                    <input
                        type="text"
                        placeholder="Enter your name"
                        value={participantName}
                        onChange={(e) => setParticipantName(e.target.value)}
                        className="text-lg p-2 border rounded"
                        required
                    />
                    <button type="submit" className="mt-4 bg-blue-500 text-white py-2 px-4 rounded">
                        Start Quiz
                    </button>
                </form>
            </div>
        )
    }

    if (showResults && quizResults) {
        return (
            <div className="max-w-4xl mx-auto p-6">
                <div className="bg-white rounded-lg shadow-lg p-8">
                    <div className="text-center mb-8">
                        <h1 className="text-3xl font-bold mb-4">Quiz Results</h1>
                        <div className="text-6xl font-bold mb-4">
                            {quizResults.score}%
                        </div>
                        <div className="text-xl text-gray-600 mb-6">
                            You got {quizResults.correct_answers} out of {quizResults.total_questions} questions correct
                        </div>
                        <div className="bg-gray-100 rounded-lg p-4 mb-6">
                            <div className="text-lg font-semibold mb-2">Summary</div>
                            <div className="grid grid-cols-2 gap-4 text-center">
                                <div>
                                    <div className="text-2xl font-bold text-green-600">{quizResults.correct_answers}</div>
                                    <div className="text-sm text-gray-600">Correct</div>
                                </div>
                                <div>
                                    <div className="text-2xl font-bold text-red-600">{quizResults.total_questions - quizResults.correct_answers}</div>
                                    <div className="text-sm text-gray-600">Incorrect</div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="space-y-6">
                        <h2 className="text-2xl font-bold mb-4">Question Review</h2>
                        {quizResults.feedback.map((item, index) => (
                            <div key={item.question_id} className="border rounded-lg p-6">
                                <div className="flex items-start justify-between mb-4">
                                    <h3 className="text-lg font-semibold">
                                        Question {index + 1}
                                    </h3>
                                    <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                                        item.is_correct 
                                            ? 'bg-green-100 text-green-800' 
                                            : 'bg-red-100 text-red-800'
                                    }`}>
                                        {item.is_correct ? 'Correct' : 'Incorrect'}
                                    </div>
                                </div>
                                
                                <p className="text-gray-700 mb-4">{item.question_text}</p>
                                
                                <div className="space-y-2">
                                    <div className="flex items-center">
                                        <span className="font-medium w-24">Your answer:</span>
                                        <span className={`ml-2 ${item.is_correct ? 'text-green-600' : 'text-red-600'}`}>
                                            {item.participant_answer || 'No answer'}
                                        </span>
                                    </div>
                                    
                                    {!item.is_correct && (
                                        <div className="flex items-center">
                                            <span className="font-medium w-24">Correct answer:</span>
                                            <span className="ml-2 text-green-600 font-medium">
                                                {item.correct_answer}
                                            </span>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="text-center mt-8">
                        <button
                            onClick={() => window.location.href = '/'}
                            className="bg-blue-600 text-white py-3 px-6 rounded-lg hover:bg-blue-700 transition-colors"
                        >
                            Back to Home
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="max-w-4xl mx-auto p-6">
            <div className="bg-white rounded-lg shadow-lg p-8">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-2xl font-bold">{quiz.title}</h1>
                    <div className="bg-red-100 text-red-800 px-4 py-2 rounded-lg font-bold">
                        Time Left: {timeLeft}s
                    </div>
                </div>

                <div className="mb-6">
                    <div className="flex justify-between text-sm text-gray-600 mb-2">
                        <span>Question {currentQuestionIndex + 1} of {quiz.questions.length}</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                        <div
                            className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                            style={{
                                width: `${((currentQuestionIndex + 1) / quiz.questions.length) * 100}%`
                            }}
                        ></div>
                    </div>
                </div>

                <div className="mb-8">
                    <h2 className="text-xl font-semibold mb-6">
                        {currentQuestion?.question_text}
                    </h2>

                    <div className="space-y-3">
                        {currentQuestion?.choices.map((choice, index) => (
                            <button
                                key={choice.id}
                                onClick={() => handleAnswerSelect(choice.id)}
                                className={`w-full p-4 text-left border-2 rounded-lg transition-all ${
                                    selectedChoice === choice.id
                                        ? 'border-blue-500 bg-blue-50'
                                        : 'border-gray-200 hover:border-gray-300'
                                }`}
                            >
                                <span className="font-medium mr-3">
                                    {String.fromCharCode(65 + index)}.
                                </span>
                                {choice.text}
                            </button>
                        ))}
                    </div>
                </div>

                <div className="flex justify-end">
                    <button
                        onClick={() => handleNextQuestion(false)}
                        disabled={!selectedChoice || isSubmitting}
                        className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {currentQuestionIndex === quiz.questions.length - 1
                            ? (isSubmitting ? 'Submitting...' : 'Finish')
                            : 'Next'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default QuizPlayer;
