import api from './api';

export const authService = {
  async login(credentials) {
    const response = await api.post('/token-login', credentials);
    // Save the token to localStorage
    localStorage.setItem('token', response.data.token);
    return response.data;
  },

  async register(userData) {
    // Get CSRF cookie first
    await api.get('/sanctum/csrf-cookie', { baseURL: process.env.REACT_APP_API_URL?.replace(/\/api$/, '') || 'http://localhost:8000' });
    const response = await api.post('/register', userData);
    return response.data;
  },

  logout() {
    // No token to remove, but you may want to call a logout endpoint
  },
};
