import React, { useState, useEffect } from 'react';

const QuizForm = ({ onSubmit, initialData = null, isEditing = false }) => {
    const [quiz, setQuiz] = useState({
        title: '',
        description: '',
        max_attempts: 3,
        time_per_question: 30,
        pin: ''
    });
    const [errors, setErrors] = useState({});

    useEffect(() => {
        if (initialData) {
            setQuiz({
                title: initialData.title || '',
                description: initialData.description || '',
                max_attempts: initialData.max_attempts || 3,
                time_per_question: initialData.time_per_question || 30,
                pin: initialData.pin || ''
            });
        }
    }, [initialData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setQuiz(prevQuiz => ({ ...prevQuiz, [name]: value }));
    };

    const validateForm = () => {
        const newErrors = {};
        if (!quiz.title.trim()) newErrors.title = 'Title is required.';
        if (quiz.pin && (quiz.pin.length < 4 || quiz.pin.length > 10)) {
            newErrors.pin = 'PIN must be between 4 and 10 characters.';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (validateForm()) {
            await onSubmit(quiz);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4 max-w-lg mx-auto bg-white p-8 shadow rounded-lg">
            <div>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700">Title</label>
                <input
                    type="text"
                    name="title"
                    id="title"
                    value={quiz.title}
                    onChange={handleChange}
                    className={`mt-1 block w-full px-3 py-2 border ${errors.title ? 'border-red-500' : 'border-gray-300'} rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500`}
                />
                {errors.title && <p className="text-red-500 text-xs mt-1">{errors.title}</p>}
            </div>

            <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                    name="description"
                    id="description"
                    value={quiz.description}
                    onChange={handleChange}
                    rows="3"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                ></textarea>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="max_attempts" className="block text-sm font-medium text-gray-700">Max Attempts</label>
                    <input
                        type="number"
                        name="max_attempts"
                        id="max_attempts"
                        value={quiz.max_attempts}
                        onChange={handleChange}
                        min="1"
                        max="10"
                        className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    />
                </div>
                <div>
                    <label htmlFor="time_per_question" className="block text-sm font-medium text-gray-700">Time per Question (seconds)</label>
                    <input
                        type="number"
                        name="time_per_question"
                        id="time_per_question"
                        value={quiz.time_per_question}
                        onChange={handleChange}
                        min="5"
                        max="300"
                        className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    />
                </div>
            </div>

            <div>
                <label htmlFor="pin" className="block text-sm font-medium text-gray-700">PIN (optional)</label>
                <input
                    type="text"
                    name="pin"
                    id="pin"
                    value={quiz.pin}
                    onChange={handleChange}
                    className={`mt-1 block w-full px-3 py-2 border ${errors.pin ? 'border-red-500' : 'border-gray-300'} rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500`}
                    placeholder="4-10 characters"
                />
                {errors.pin && <p className="text-red-500 text-xs mt-1">{errors.pin}</p>}
            </div>

            <div className="flex justify-end">
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                >
                    {isEditing ? 'Update Quiz' : 'Create Quiz'}
                </button>
            </div>
        </form>
    );
};

export default QuizForm;
