import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import { useAuth } from '../../context/AuthContext';

const Header = () => {
    // const { user, logout } = useAuth();
    const user = null; // Mock user state
    const navigate = useNavigate();

    const handleLogout = () => {
        // logout();
        navigate('/login');
    };

    return (
        <header className="bg-white/90 shadow-lg backdrop-blur sticky top-0 z-50">
            <nav className="container mx-auto px-6 py-4 flex justify-between items-center rounded-b-xl">
                <Link to="/" className="text-2xl font-extrabold tracking-tight text-gray-800 hover:text-gray-600 transition-colors">
                    QuizBm
                </Link>
                <div className="flex items-center space-x-4">
                    {user ? (
                        <>
                            <Link to="/dashboard" className="text-gray-600 hover:text-gray-900 font-medium transition-colors">
                                Dashboard
                            </Link>
                            <span className="text-gray-500">Welcome, {user.name}!</span>
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 bg-red-500 text-white rounded-lg shadow hover:bg-red-600 transition-colors font-semibold"
                            >
                                Logout
                            </button>
                        </>
                    ) : (
                        <>
                            <Link to="/login" className="text-gray-600 hover:text-gray-900 font-medium transition-colors">
                                Login
                            </Link>
                            <Link to="/register" className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition-colors font-semibold">
                                Register
                            </Link>
                        </>
                    )}
                </div>
            </nav>
        </header>
    );
};

export default Header;
