import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import Header from './components/common/Header';
// import ProtectedRoute from './components/common/ProtectedRoute';

import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import DashboardPage from './pages/DashboardPage';
import CreateQuizPage from './pages/CreateQuizPage';
import EditQuizPage from './pages/EditQuizPage';
import PlayQuizPage from './pages/PlayQuizPage';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="min-h-screen bg-gradient-to-br from-gray-50 via-gray-100 to-gray-200 font-sans text-gray-800">
          <Header />
          <main className="container mx-auto mt-8 px-4 py-6 rounded-lg shadow-lg bg-white/80 backdrop-blur-md">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />
              <Route path="/q/:slug" element={<PlayQuizPage />} />

              {/* <Route path="/dashboard" element={<ProtectedRoute><DashboardPage /></ProtectedRoute>} /> */}
              {/* <Route path="/create-quiz" element={<ProtectedRoute><CreateQuizPage /></ProtectedRoute>} /> */}
              {/* <Route path="/edit-quiz/:id" element={<ProtectedRoute><EditQuizPage /></ProtectedRoute>} /> */}

              {/* For now, let's make dashboard accessible for testing */}
              <Route path="/dashboard" element={<DashboardPage />} />

            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
