import React, { useState, useEffect, useMemo } from 'react';
import { useTimer } from '../../hooks/useTimer';
import quizService from '../../services/quiz';

const QuizPlayer = ({ quiz, onFinish }) => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answers, setAnswers] = useState({});
    const [selectedChoice, setSelectedChoice] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [participantName, setParticipantName] = useState('');
    const [hasStarted, setHasStarted] = useState(false);

    const currentQuestion = useMemo(() => quiz.questions[currentQuestionIndex], [quiz, currentQuestionIndex]);

    const { timeLeft, startTimer, resetTimer } = useTimer(
        currentQuestion?.time_per_question || 30,
        () => handleNextQuestion(true) // auto-submit on time up
    );

    useEffect(() => {
        if (hasStarted && currentQuestion) {
            resetTimer(currentQuestion.time_per_question || quiz.time_per_question || 30);
            startTimer();
            setSelectedChoice(null);
        }
    }, [currentQuestion, hasStarted, quiz.time_per_question, resetTimer, startTimer]);

    const handleAnswerSelect = (choiceId) => {
        setSelectedChoice(choiceId);
    };

    const handleNextQuestion = (isTimeUp = false) => {
        const answer = isTimeUp ? null : selectedChoice;
        setAnswers(prev => ({ ...prev, [currentQuestion.id]: answer }));

        if (currentQuestionIndex < quiz.questions.length - 1) {
            setCurrentQuestionIndex(prev => prev + 1);
        } else {
            submitQuiz();
        }
    };

    const submitQuiz = async () => {
        setIsSubmitting(true);
        const finalAnswers = { ...answers };
        if (selectedChoice) {
            finalAnswers[currentQuestion.id] = selectedChoice;
        }

        try {
            const response = await quizService.submitAttempt(quiz.slug, {
                participant_name: participantName,
                answers: finalAnswers,
                time_taken: 1 // placeholder
            });
            onFinish(response.data.score);
        } catch (error) {
            console.error('Error submitting quiz:', error);
            alert('There was an error submitting your quiz.');
        } finally {
            setIsSubmitting(false);
        }
    };
    
    const handleStart = (e) => {
        e.preventDefault();
        if(participantName.trim()){
            setHasStarted(true);
        }
    }

    if (!hasStarted) {
        return (
            <div className="text-center">
                <form onSubmit={handleStart}>
                    <input
                        type="text"
                        placeholder="Enter your name"
                        value={participantName}
                        onChange={(e) => setParticipantName(e.target.value)}
                        className="text-lg p-2 border rounded"
                        required
                    />
                    <button type="submit" className="mt-4 bg-blue-500 text-white py-2 px-4 rounded">
                        Start Quiz
                    </button>
                </form>
            </div>
        )
    }

    return (
        <div className="max-w-4xl mx-auto p-6">
            <div className="bg-white rounded-lg shadow-lg p-8">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-2xl font-bold">{quiz.title}</h1>
                    <div className="bg-red-100 text-red-800 px-4 py-2 rounded-lg font-bold">
                        Time Left: {timeLeft}s
                    </div>
                </div>

                <div className="mb-6">
                    <div className="flex justify-between text-sm text-gray-600 mb-2">
                        <span>Question {currentQuestionIndex + 1} of {quiz.questions.length}</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                        <div
                            className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                            style={{
                                width: `${((currentQuestionIndex + 1) / quiz.questions.length) * 100}%`
                            }}
                        ></div>
                    </div>
                </div>

                <div className="mb-8">
                    <h2 className="text-xl font-semibold mb-6">
                        {currentQuestion?.question_text}
                    </h2>

                    <div className="space-y-3">
                        {currentQuestion?.choices.map((choice, index) => (
                            <button
                                key={choice.id}
                                onClick={() => handleAnswerSelect(choice.id)}
                                className={`w-full p-4 text-left border-2 rounded-lg transition-all ${
                                    selectedChoice === choice.id
                                        ? 'border-blue-500 bg-blue-50'
                                        : 'border-gray-200 hover:border-gray-300'
                                }`}
                            >
                                <span className="font-medium mr-3">
                                    {String.fromCharCode(65 + index)}.
                                </span>
                                {choice.text}
                            </button>
                        ))}
                    </div>
                </div>

                <div className="flex justify-end">
                    <button
                        onClick={() => handleNextQuestion(false)}
                        disabled={!selectedChoice || isSubmitting}
                        className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {currentQuestionIndex === quiz.questions.length - 1
                            ? (isSubmitting ? 'Submitting...' : 'Finish')
                            : 'Next'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default QuizPlayer;
