import React from 'react';
import { useNavigate } from 'react-router-dom';
import QuizForm from '../components/quiz/QuizForm';
import quizService from '../services/quiz';

const CreateQuizPage = () => {
    const navigate = useNavigate();

    const handleCreateQuiz = async (quizData) => {
        try {
            const newQuiz = await quizService.createQuiz({data: quizData});
            alert('Quiz created successfully!');
            navigate(`/edit-quiz/${newQuiz.data.id}`); // Redirect to edit page to add questions
        } catch (error) {
            console.error('Failed to create quiz:', error);
            alert('Failed to create quiz. Please check the console for details.');
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4 text-center">Create a New Quiz</h1>
            <QuizForm onSubmit={handleCreateQuiz} />
        </div>
    );
};

export default CreateQuizPage;
